mapper.select:
		SELECT id, mname, tel, email, zipcode, address1, address2,
		fname, r
		from(
		SELECT id, mname, tel, email, zipcode, address1, address2,
		fname, rownum r
		from(
		SELECT id, mname, tel, email, zipcode, address1,
		address2, fname
		FROM member



					mname like '%'||#{word}||'%'


					id like '%'||#{word}||'%'


					email like '%'||#{word}||'%'



		ORDER BY mdate DESC
		)

                )where r >= #{sno} and r <= #{eno}
