mapper.select:
	select
    count(*) from zagook_member
     where email = #{email}
     and password = #{password}

	select
    count(*) from zagook_member
     where email = #{email}
     and password = #{password}

SELECT COUNT(id)
FROM zagook_member
WHERE id=#{id}

SELECT COUNT(email)
FROM zagook_member
WHERE email=#{email}

mapper.update:
	update zagook_member
      set id = #{id},
      mname = #{mname},
      tel = #{tel},
      job = #{job}
      where email = #{email}
